INSERT INTO `blog_options` VALUES ('137', '_transient_dash_01e18dead815ce736e3b9cccfbd773a5', '<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2017/05/wordpress-4-7-5/\'>WordPress 4.7.5 Security and Maintenance Release</a> <span class=\"rss-date\">16 de maio de 2017</span><div class=\"rssSummary\">WordPress 4.7.5 is now available. This is a security release for all previous versions and we strongly encourage you to update your sites immediately. WordPress versions 4.7.4 and earlier are affected by six security issues: Insufficient redirect validation in the HTTP class. Reported by Ronni Skansing. Improper handling of post meta data values in the XML-RPC [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wptavern.com/atom-editor-adds-git-and-github-integration\'>WPTavern: Atom Editor Adds Git and GitHub Integration</a></li><li><a class=\'rsswidget\' href=\'https://wptavern.com/wpweekly-episode-273-mental-health-awareness-with-bridget-willard-and-ed-finkler\'>WPTavern: WPWeekly Episode 273 – Mental Health Awareness With Bridget Willard and Ed Finkler</a></li><li><a class=\'rsswidget\' href=\'https://wptavern.com/wordcamp-europe-contributor-day-to-host-13-workshops-event-is-nearly-sold-out\'>WPTavern: WordCamp Europe Contributor Day to Host 13 Workshops, Event is Nearly Sold Out</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\"dashboard-news-plugin\"><span>Plugins populares:</span> WooCommerce&nbsp;<a href=\"plugin-install.php?tab=plugin-information&amp;plugin=woocommerce&amp;_wpnonce=7f2f9262d6&amp;TB_iframe=true&amp;width=600&amp;height=800\" class=\"thickbox open-plugin-details-modal\" aria-label=\"Instalar WooCommerce\">(Instalar)</a></li></ul></div>', 'no');
